{%- if product.type == 'Saree' -%}
    {% assign icon_count = 1 %}
    {% assign icon_hand_woven = false %}
    {% assign icon_silk_mark = false %}
    {% assign icon_hand_paint = false %}
    {% assign icon_printed = false %}
    {% assign icon_hand_embroidery = false %}
    {% assign icon_pure_cotton = false %}
    {% assign icon_free_shipping = false %}
    {% assign icon_on_sale = false %}
    {% assign work_value = product.metafields.saree.new_work | metafield_text %}
    {% assign fabric_value = product.metafields.saree.fabric_type | metafield_text %}
    {% assign bp_value = product.metafields.saree.BlousePiece  %}
        {%- if product.metafields.saree.new_work != blank -%}
            {%- if work_value contains 'Hand Woven' -%}
  				{% assign icon_count = icon_count | plus: 1 %}
                {% assign icon_hand_woven = true  %}
            {%- endif -%}
            {%- if work_value contains 'Hand Paint' -%}
  				{% assign icon_count = icon_count | plus: 1 %}
                {% assign icon_hand_paint = true %}
            {%-endif-%}
            {% if work_value contains 'Print' %}
  				{% assign icon_count = icon_count | plus: 1 %}
                {% assign icon_printed = true %}
            {%-endif-%}
            {% if work_value contains 'Hand Embroidery' %}
  				{% assign icon_count = icon_count | plus: 1 %}
                {% assign icon_hand_embroidery = true %}
            {%-endif-%}
		{%-endif-%}
        {%- if product.metafields.saree.fabric_type != blank -%}
            {% if fabric_value contains 'Silk Mark Certified' %}
  				{% assign icon_count = icon_count | plus: 1 %}
                {% assign icon_silk_mark = true %}
            {%-endif-%}
            {% if fabric_value == 'Cotton' %}
  				{% assign icon_count = icon_count | plus: 1 %}
                {% assign icon_pure_cotton = true %}
            {%-endif-%}
		{%-endif-%}
        {%- if product.metafields.saree.BlousePiece != blank -%}
            {% if bp_value contains 'Contrast' %}
  				{% assign icon_count = icon_count | plus: 1 %}
                {% assign icon_contrast_bp = true %}
            {%-endif-%}
            {% if bp_value contains 'Matching' %}
  				{% assign icon_count = icon_count | plus: 1 %}
                {% assign icon_matching_bp = true %}
            {%-endif-%}
		{%-endif-%}
		{% for tag in product.tags %}
            {% if tag contains 'sale' %}
  				{% assign icon_count = icon_count | plus: 1 %}
                {% assign icon_on_sale = true %}
            {%-endif-%}
        {% endfor %}
